script:
frappe.ui.form.on('Employee', {
	refresh: function(frm) {
	    var fields = ['custom_religion','custom_witness_name_1','custom_witness_name_2','custom_whatsapp_no','custom_address_line_1','custom_address_line_2','custom_address_line_2_permanent','custom_address_line_1_permanent','custom_canteen_card_no','custom_aadhaar_number','custom_uan_number','custom_bank_branch','custom_salary_structure','custom_father_occupation']
		fields.forEach(function(field) {
            var $field = frm.fields_dict[field];
            if ($field && $field.$input) {
                $field.$input.css({'height': '33px'});
            }
        });
	}
})
